%% read .avi videos and save as .mat filesrootfolder = uigetdir;folders = dir(rootfolder);nItems = length(folders);for n = 1:nItems    m = nItems + 1 - n;    if folders(m).isdir == 0        folders(m) = [];    endendfor n = 1:37    fprintf('saving the %d th video\n', n)    foldername = [rootfolder '\' folders(n+2).name];    movies = dir(foldername);    for m = 3:5    vidname = movies(m).name;    vidObj = VideoReader([foldername,'\',vidname]);    v = read(vidObj);    v = squeeze(v);    save(vidname(1:end-4),'v','-v7.3');    endend